(7126, 619, 'description_list_0_short_description', 'Wanda Studios Qingdao, the centerpiece of the Movie Metropolis, is a <strong>US $8.2 billion commercial property investment by its parent company, the Wanda Group.</strong>'),
(7127, 619, '_description_list_0_short_description', 'field_576bcfd7fd94f'),
(7128, 619, 'description_list_1_icon', 'icon-20'),
(7129, 619, '_description_list_1_icon', 'field_576bcf1efd94e'),
(7130, 619, 'description_list_1_short_description', '<strong>Spread across 165.41 hectares, </strong>this large-scale movie studio features a <strong>substantial number of state-of-the-art sound stages, </strong>built to <strong>the highest international technical standards.</strong>'),
(7131, 619, '_description_list_1_short_description', 'field_576bcfd7fd94f'),
