(7143, 619, '_blockquote_background_image', 'field_576bd05bfd953'),
(7144, 619, 'blockquote_list_0_blockquote_text', '<strong>Chairman Wang Jianlin </strong> always had a dream: he wanted a Chinese Hollywood. He now scripts his Hollywood dreams, <strong>building the Movie Metropolis </strong> . It is the <strong>largest entertainment infrastructure project ever undertaken</strong>'),
(7145, 619, '_blockquote_list_0_blockquote_text', 'field_576bd089fd955'),
(7146, 619, 'blockquote_list_0_author', ''),
(7147, 619, '_blockquote_list_0_author', 'field_576bd095fd956'),
(7148, 619, 'blockquote_list_0_position', ''),
(7149, 619, '_blockquote_list_0_position', 'field_576bd09dfd957'),
(7150, 619, 'blockquote_list_1_blockquote_text', 'The Movie Metropolis will be the most advanced movie studio with the largest investment in the world. Wanda has invited the world’s leading companies and talent to develop this project. I believe that with its opening… it will become one of the major centers for global film.'),
(7151, 619, '_blockquote_list_1_blockquote_text', 'field_576bd089fd955'),
(7152, 619, 'blockquote_list_1_author', 'Wang Jianlin'),
(7153, 619, '_blockquote_list_1_author', 'field_576bd095fd956'),
(7154, 619, 'blockquote_list_1_position', 'Chairman of the Wanda Group'),
(7155, 619, '_blockquote_list_1_position', 'field_576bd09dfd957'),
(7156, 619, 'blockquote_list', '2'),
(7157, 619, '_blockquote_list', 'field_576bd06dfd954'),
