(8696, 689, '_description_list_1_icon', 'field_576bcf1efd94e'),
(8697, 689, 'description_list_1_short_description', '<strong>Spread across 165.41 hectares, </strong>this large-scale movie studio features a <strong>substantial number of state-of-the-art sound stages, </strong>built to <strong>the highest international technical standards.</strong>'),
(8698, 689, '_description_list_1_short_description', 'field_576bcfd7fd94f'),
