(8701, 689, 'description_list_2_short_description', 'A large backlot will house built-to-order shooting locations with <strong>infinite capacity to customize to production needs.</strong>'),
(8702, 689, '_description_list_2_short_description', 'field_576bcfd7fd94f'),
