(8710, 689, '_blockquote_background_image', 'field_576bd05bfd953'),
(8711, 689, 'blockquote_list_0_blockquote_text', '<strong>Chairman Wang Jianlin </strong> always had a dream: he wanted a Chinese Hollywood. He now scripts his Hollywood dreams, <strong>building the Movie Metropolis </strong> . It is the <strong>largest entertainment infrastructure project ever undertaken</strong>'),
(8712, 689, '_blockquote_list_0_blockquote_text', 'field_576bd089fd955'),
(8713, 689, 'blockquote_list_0_author', ''),
(8714, 689, '_blockquote_list_0_author', 'field_576bd095fd956'),
(8715, 689, 'blockquote_list_0_position', ''),
(8716, 689, '_blockquote_list_0_position', 'field_576bd09dfd957'),
(8717, 689, 'blockquote_list_1_blockquote_text', 'The Movie Metropolis will be the most advanced movie studio with the largest investment in the world. Wanda has invited the world’s leading companies and talent to develop this project. I believe that with its opening… it will become one of the major centers for global film.'),
(8718, 689, '_blockquote_list_1_blockquote_text', 'field_576bd089fd955'),
