(8720, 689, '_blockquote_list_1_author', 'field_576bd095fd956'),
(8721, 689, 'blockquote_list_1_position', 'Chairman of the Wanda Group'),
(8722, 689, '_blockquote_list_1_position', 'field_576bd09dfd957'),
(8723, 689, 'blockquote_list', '2'),
