(8744, 689, '_why_title', 'field_576bd164fd964'),
(8745, 689, 'why_features_0_icon', 'icon-23'),
(8746, 689, '_why_features_0_icon', 'field_576bd17ffd966'),
(8747, 689, 'why_features_0_title', 'Easy and Efficient'),
(8748, 689, '_why_features_0_title', 'field_576bd187fd967'),
(8749, 689, 'why_features_0_texts_0_content', 'Wanda Studios offers a comprehensive package that drives cost-savings across all areas of production.\r\n                            '),
(8750, 689, '_why_features_0_texts_0_content', 'field_576bd19bfd969'),
(8751, 689, 'why_features_0_texts_1_content', 'All services are tailored to suit individual productions, customized offerings available.\r\n                            '),
(8752, 689, '_why_features_0_texts_1_content', 'field_576bd19bfd969'),
(8753, 689, 'why_features_0_texts_2_content', 'Wanda Studios aims to ease concerns related to tax, immigration, banking, union negotiations and so on.\r\n                            '),
(8754, 689, '_why_features_0_texts_2_content', 'field_576bd19bfd969'),
(8755, 689, 'why_features_0_texts', '3'),
(8756, 689, '_why_features_0_texts', 'field_576bd18efd968'),
(8757, 689, 'why_features_1_icon', 'icon-24'),
(8758, 689, '_why_features_1_icon', 'field_576bd17ffd966'),
(8759, 689, 'why_features_1_title', 'International Workforce'),
(8760, 689, '_why_features_1_title', 'field_576bd187fd967'),
(8761, 689, 'why_features_1_texts_0_content', 'The strong work ethic of Qingdao film crews stand unrivalled. Expertise areas include scripting, location scouting, cinematography and photography (DoPs), costume design, stunts and lighting.\r\n                            '),
(8762, 689, '_why_features_1_texts_0_content', 'field_576bd19bfd969'),
(8763, 689, 'why_features_1_texts_1_content', 'Qingdao boasts professional and committed specialists with international experience in almost any area.\r\n                            '),
(8764, 689, '_why_features_1_texts_1_content', 'field_576bd19bfd969'),
(8765, 689, 'why_features_1_texts', '2'),
(8766, 689, '_why_features_1_texts', 'field_576bd18efd968'),
(8767, 689, 'why_features_2_icon', 'icon-25'),
(8768, 689, '_why_features_2_icon', 'field_576bd17ffd966'),
(8769, 689, 'why_features_2_title', 'Comprehensive<br>Production Incentives'),
(8770, 689, '_why_features_2_title', 'field_576bd187fd967'),
(8771, 689, 'why_features_2_texts_0_content', 'The Qingdao government and the Wanda Group have jointly established a RMB 5 billion Qingdao film and television industry development fund to be realized over a five year period.\r\n                            '),
(8772, 689, '_why_features_2_texts_0_content', 'field_576bd19bfd969'),
(8773, 689, 'why_features_2_texts_1_content', 'The incentives fund invests in qualified film and television projects, offering grants to film and television content produced on the studio lot in Qingdao.\r\n                            '),
(8774, 689, '_why_features_2_texts_1_content', 'field_576bd19bfd969'),
(8775, 689, 'why_features_2_texts', '2'),
(8776, 689, '_why_features_2_texts', 'field_576bd18efd968'),
(8777, 689, 'why_features_3_icon', 'icon-26'),
(8778, 689, '_why_features_3_icon', 'field_576bd17ffd966'),
(8779, 689, 'why_features_3_title', 'Information Security'),
(8780, 689, '_why_features_3_title', 'field_576bd187fd967'),
(8781, 689, 'why_features_3_texts_0_content', 'Wanda Studios provides world-class security services, ensuring every production in China remains confidential, private, and fully protected from security breach and property loss.\r\n                            '),
(8782, 689, '_why_features_3_texts_0_content', 'field_576bd19bfd969'),
(8783, 689, 'why_features_3_texts_1_content', 'The Studios strive to future proof its IT infrastructure, drawing merit from domestic and international cutting-edge techniques to build a state-of-the-art system that prevents hostile attacks.\r\n                            '),
