(15028, 938, '_why_features_2_title', 'field_576bd187fd967'),
(15029, 938, 'why_features_2_texts_0_content', 'The Qingdao government and the Wanda Group have jointly established a RMB 5 billion Qingdao film and television industry development fund to be realized over a five year period.\r\n                            '),
(15030, 938, '_why_features_2_texts_0_content', 'field_576bd19bfd969'),
