(16456, 971, 'why_title', 'Why Film <br>With Wanda Studios'),
(16457, 971, '_why_title', 'field_577ba2b9e3410'),
(16458, 971, 'why_features_0_icon', 'icon-23'),
(16459, 971, '_why_features_0_icon', 'field_577ba2d4e3412'),
(16460, 971, 'why_features_0_title', 'Easy and Efficient'),
(16461, 971, '_why_features_0_title', 'field_577ba2dfe3413'),
(16462, 971, 'why_features_0_texts_0_content', 'Wanda Studios offers a comprehensive package that drives cost-savings across all areas of production.'),
(16463, 971, '_why_features_0_texts_0_content', 'field_577ba2f3e3415'),
(16464, 971, 'why_features_0_texts_1_content', 'All services are tailored to suit individual productions, customized offerings available.'),
(16465, 971, '_why_features_0_texts_1_content', 'field_577ba2f3e3415'),
(16466, 971, 'why_features_0_texts_2_content', 'Wanda Studios aims to ease concerns related to tax, immigration, banking, union negotiations and so on.'),
(16467, 971, '_why_features_0_texts_2_content', 'field_577ba2f3e3415'),
(16468, 971, 'why_features_0_texts', '3'),
(16469, 971, '_why_features_0_texts', 'field_577ba2e5e3414'),
(16470, 971, 'why_features_1_icon', 'icon-24'),
(16471, 971, '_why_features_1_icon', 'field_577ba2d4e3412'),
(16472, 971, 'why_features_1_title', 'International Workforce'),
(16473, 971, '_why_features_1_title', 'field_577ba2dfe3413'),
(16474, 971, 'why_features_1_texts_0_content', 'The strong work ethic of Qingdao film crews stand unrivalled. Expertise areas include scripting, location scouting, cinematography and photography (DoPs), costume design, stunts and lighting.'),
(16475, 971, '_why_features_1_texts_0_content', 'field_577ba2f3e3415'),
(16476, 971, 'why_features_1_texts_1_content', 'Qingdao boasts professional and committed specialists with international experience in almost any area.'),
(16477, 971, '_why_features_1_texts_1_content', 'field_577ba2f3e3415'),
(16478, 971, 'why_features_1_texts', '2'),
(16479, 971, '_why_features_1_texts', 'field_577ba2e5e3414'),
(16480, 971, 'why_features_2_icon', 'icon-25'),
(16481, 971, '_why_features_2_icon', 'field_577ba2d4e3412'),
(16482, 971, 'why_features_2_title', 'Comprehensive<br>Production Incentives'),
(16483, 971, '_why_features_2_title', 'field_577ba2dfe3413'),
(16484, 971, 'why_features_2_texts_0_content', 'The Qingdao government and the Wanda Group have jointly established a RMB 5 billion Qingdao film and television industry development fund to be realized over a five year period.'),
(16485, 971, '_why_features_2_texts_0_content', 'field_577ba2f3e3415'),
(16486, 971, 'why_features_2_texts_1_content', 'The incentives fund invests in qualified film and television projects, offering grants to film and television content produced on the studio lot in Qingdao.'),
(16487, 971, '_why_features_2_texts_1_content', 'field_577ba2f3e3415'),
(16488, 971, 'why_features_2_texts', '2'),
(16489, 971, '_why_features_2_texts', 'field_577ba2e5e3414'),
