(17924, 1032, 'why_title', 'Why Film <br>With Wanda Studios'),
(17925, 1032, '_why_title', 'field_577bc708621cc'),
(17926, 1032, 'why_features_0_icon', 'icon-23'),
(17927, 1032, '_why_features_0_icon', 'field_577bc720621ce'),
(17928, 1032, 'why_features_0_title', 'Easy and Efficient'),
(17929, 1032, '_why_features_0_title', 'field_577bc728621cf'),
(17930, 1032, 'why_features_0_texts_0_content', 'Wanda Studios offers a comprehensive package that drives cost-savings across all areas of production.'),
(17931, 1032, '_why_features_0_texts_0_content', 'field_577bc73f621d1'),
(17932, 1032, 'why_features_0_texts_1_content', 'All services are tailored to suit individual productions, customized offerings available.'),
(17933, 1032, '_why_features_0_texts_1_content', 'field_577bc73f621d1'),
(17934, 1032, 'why_features_0_texts_2_content', 'Wanda Studios aims to ease concerns related to tax, immigration, banking, union negotiations and so on.'),
(17935, 1032, '_why_features_0_texts_2_content', 'field_577bc73f621d1'),
(17936, 1032, 'why_features_0_texts', '3'),
(17937, 1032, '_why_features_0_texts', 'field_577bc72d621d0'),
(17938, 1032, 'why_features_1_icon', 'icon-24'),
(17939, 1032, '_why_features_1_icon', 'field_577bc720621ce'),
(17940, 1032, 'why_features_1_title', 'International Workforce'),
(17941, 1032, '_why_features_1_title', 'field_577bc728621cf'),
(17942, 1032, 'why_features_1_texts_0_content', 'The strong work ethic of Qingdao film crews stand unrivalled. Expertise areas include scripting, location scouting, cinematography and photography (DoPs), costume design, stunts and lighting.'),
(17943, 1032, '_why_features_1_texts_0_content', 'field_577bc73f621d1'),
(17944, 1032, 'why_features_1_texts_1_content', 'Qingdao boasts professional and committed specialists with international experience in almost any area.'),
(17945, 1032, '_why_features_1_texts_1_content', 'field_577bc73f621d1'),
(17946, 1032, 'why_features_1_texts', '2'),
(17947, 1032, '_why_features_1_texts', 'field_577bc72d621d0'),
(17948, 1032, 'why_features_2_icon', 'icon-25'),
(17949, 1032, '_why_features_2_icon', 'field_577bc720621ce'),
(17950, 1032, 'why_features_2_title', 'Comprehensive<br>Production Incentives'),
(17951, 1032, '_why_features_2_title', 'field_577bc728621cf'),
(17952, 1032, 'why_features_2_texts_0_content', 'The Qingdao government and the Wanda Group have jointly established a RMB 5 billion Qingdao film and television industry development fund to be realized over a five year period.'),
(17953, 1032, '_why_features_2_texts_0_content', 'field_577bc73f621d1'),
(17954, 1032, 'why_features_2_texts_1_content', 'The incentives fund invests in qualified film and television projects, offering grants to film and television content produced on the studio lot in Qingdao.'),
(17955, 1032, '_why_features_2_texts_1_content', 'field_577bc73f621d1'),
(17956, 1032, 'why_features_2_texts', '2'),
(17957, 1032, '_why_features_2_texts', 'field_577bc72d621d0'),
(17958, 1032, 'why_features_3_icon', 'icon-26'),
(17959, 1032, '_why_features_3_icon', 'field_577bc720621ce'),
(17960, 1032, 'why_features_3_title', 'Information Security'),
(17961, 1032, '_why_features_3_title', 'field_577bc728621cf'),
(17962, 1032, 'why_features_3_texts_0_content', 'Wanda Studios provides world-class security services, ensuring every production in China remains confidential, private, and fully protected from security breach and property loss.'),
(17963, 1032, '_why_features_3_texts_0_content', 'field_577bc73f621d1'),
(17964, 1032, 'why_features_3_texts_1_content', 'The Studios strive to future proof its IT infrastructure, drawing merit from domestic and international cutting-edge techniques to build a state-of-the-art system that prevents hostile attacks.'),
