(18586, 1043, 'why_title', 'Why Film <br>With Wanda Studios'),
(18587, 1043, '_why_title', 'field_577bc708621cc'),
(18588, 1043, 'why_features_0_icon', 'icon-23'),
(18589, 1043, '_why_features_0_icon', 'field_577bc720621ce'),
(18590, 1043, 'why_features_0_title', 'Easy and Efficient'),
(18591, 1043, '_why_features_0_title', 'field_577bc728621cf'),
(18592, 1043, 'why_features_0_texts_0_content', 'Wanda Studios offers a comprehensive package that drives cost-savings across all areas of production.'),
(18593, 1043, '_why_features_0_texts_0_content', 'field_577bc73f621d1'),
(18594, 1043, 'why_features_0_texts_1_content', 'All services are tailored to suit individual productions, customized offerings available.'),
(18595, 1043, '_why_features_0_texts_1_content', 'field_577bc73f621d1'),
(18596, 1043, 'why_features_0_texts_2_content', 'Wanda Studios aims to ease concerns related to tax, immigration, banking, union negotiations and so on.'),
(18597, 1043, '_why_features_0_texts_2_content', 'field_577bc73f621d1'),
(18598, 1043, 'why_features_0_texts', '3'),
(18599, 1043, '_why_features_0_texts', 'field_577bc72d621d0'),
(18600, 1043, 'why_features_1_icon', 'icon-24'),
(18601, 1043, '_why_features_1_icon', 'field_577bc720621ce'),
(18602, 1043, 'why_features_1_title', 'International Workforce'),
(18603, 1043, '_why_features_1_title', 'field_577bc728621cf'),
(18604, 1043, 'why_features_1_texts_0_content', 'The strong work ethic of Qingdao film crews stand unrivalled. Expertise areas include scripting, location scouting, cinematography and photography (DoPs), costume design, stunts and lighting.'),
(18605, 1043, '_why_features_1_texts_0_content', 'field_577bc73f621d1'),
(18606, 1043, 'why_features_1_texts_1_content', 'Qingdao boasts professional and committed specialists with international experience in almost any area.'),
(18607, 1043, '_why_features_1_texts_1_content', 'field_577bc73f621d1'),
(18608, 1043, 'why_features_1_texts', '2'),
(18609, 1043, '_why_features_1_texts', 'field_577bc72d621d0'),
(18610, 1043, 'why_features_2_icon', 'icon-25'),
(18611, 1043, '_why_features_2_icon', 'field_577bc720621ce'),
(18612, 1043, 'why_features_2_title', 'Comprehensive<br>Production Incentives'),
(18613, 1043, '_why_features_2_title', 'field_577bc728621cf'),
(18614, 1043, 'why_features_2_texts_0_content', 'The Qingdao government and the Wanda Group have jointly established a RMB 5 billion Qingdao film and television industry development fund to be realized over a five year period.'),
(18615, 1043, '_why_features_2_texts_0_content', 'field_577bc73f621d1'),
(18616, 1043, 'why_features_2_texts_1_content', 'The incentives fund invests in qualified film and television projects, offering grants to film and television content produced on the studio lot in Qingdao.'),
(18617, 1043, '_why_features_2_texts_1_content', 'field_577bc73f621d1'),
(18618, 1043, 'why_features_2_texts', '2'),
(18619, 1043, '_why_features_2_texts', 'field_577bc72d621d0'),
(18620, 1043, 'why_features_3_icon', 'icon-26'),
(18621, 1043, '_why_features_3_icon', 'field_577bc720621ce'),
(18622, 1043, 'why_features_3_title', 'Information Security'),
(18623, 1043, '_why_features_3_title', 'field_577bc728621cf'),
